/**
 * API Error Utilities
 * Generated by API Builder - do not edit manually
 */

export class ApiException extends Error {
  constructor(
    public readonly response: Response,
    message: string
  ) {
    super(`HTTP ${response.status}: ${message}`);
    this.name = 'ApiException';
  }
}

export class Util {
  static async mustParse<T>(response: Response, name: string): Promise<T> {
    try {
      const data = await response.json();
      return data as T;
    } catch (error) {
      throw new ApiException(
        response,
        `Unable to parse response body as a ${name}: ${error instanceof Error ? error.message : String(error)}`
      );
    }
  }

  static async mustParseArray<T>(response: Response, name: string): Promise<T[]> {
    try {
      const data = await response.json();
      if (!Array.isArray(data)) {
        throw new ApiException(
          response,
          `Unable to parse response body. Expected an array but found a ${typeof data}`
        );
      }
      return data as T[];
    } catch (error) {
      if (error instanceof ApiException) {
        throw error;
      }
      throw new ApiException(
        response,
        `Unable to parse response body as a ${name}[]: ${error instanceof Error ? error.message : String(error)}`
      );
    }
  }
}